import React from "react";
class ClaRefsAno extends React.Component {

    showData = () => {
        // console.log(this);
        const { input } = this
        alert(input.value)
    }
    showData1 =() =>{
        const {input1} = this
        alert(input1.value)
    }

    showData2 = (event)=>{
        alert(event.target.value);
    }
    render() {
        return (
            <div>
                <input ref={()=>{console.log(this);}} type="text" placeholder="click for data"></input>
                <button onClick={this.showData}>click me for reminding data</button>
                <input ref= {console.log(this)} onBlur={this.showData1} type="text" placeholder="lose for data"></input>
                <input onClick={this.showData2} placeholder= "click for simple data receiving"></input>
            </div>


        )
    }
}

export default ClaRefsAno;
