import React from "react";
class ClaRefsAno extends React.Component {

    showData = () => {
        // console.log(this);
        const { input } = this
        alert(input.value)
    }


    showData2 = (event)=>{
        alert(event.target.value);
    }
    render() {
        return (
            <div>
                <input ref={(a)=>{this.input = a}} type="text" placeholder="click for data"></input>
                <button onClick={this.showData}>click me for reminding data</button>
                <input onClick={this.showData2} placeholder= "click for simple data receiving"></input>

                <form action="">

                </form>
            </div>


        )
    }
}

export default ClaRefsAno;
