import React from "react";
class ClaRefs extends React.Component {

    showData = () => {
        // console.log(this);
        const { input } = this.refs;
        alert(input.value)
    }
    showData1 =() =>{
        const {input1} = this.refs;
        alert()
    }
    render() {
        return (
            <div>
                <input ref="input" type="text" jplaceholder="click for data"></input>
                <button onClick={this.showData}>click me for reminding data</button>
                <input ref= "input1" onBlur={this.showData1} type="text" placeholder="lose for data"></input>
            </div>


        )
    }
}

export default ClaRefs;
